command:
await foundry.applications.api.DialogV2.prompt({
    window: { title: "Rentrer l'UUID de l'acteur" },
    content: '<input name="uuid" type="text">',
    ok: {
      label: "Valider",
      callback: async (event, button, dialog) => {
        const uuid = $(button.form.elements.uuid).val();

        const actor = await fromUuid(uuid);

        const create = await Item.create({
              name: actor.name,
              type: "gear",
              img: actor.img,
              system: foundry.utils.mergeObject(actor.system, {type:"VEHICLES", subtype:"CARS"}),
            });

      }
    }
  });
